import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './Search.css';
import PromotionList from "../List/List";

const PromotionSearch = () => {
    const [promotions, setPromotions] = useState([]);
    const [search, setSearch] = useState('');

    useEffect(() => {
        const params = {};
        if(search){
            params.title_like = search;
        }
        axios.get('http://localhost:5000/promotions?_embed=comments&_order=asc&_sorte', {params}).then((response) => {
        setPromotions(response.data);
      });
    }, [search]);

    return (
      <div className="promotion-search">
        <header className="promotion-search__header">
        <h1>Promo Show</h1>
        <Link to="/create">Nova Promoção</Link>
        </header>
        <input className="promotion-search__input" type="search" placeholder="Buscar" value={search} onChange={(event) => setSearch(event.target.value)}/>
        <PromotionList promotions={promotions} loading={!promotions.length} />
      </div>
    );
};

export default PromotionSearch;
